INSERT INTO notices (title, content, pinned, published, view_count, created_at, updated_at) VALUES
('시스템 점검 안내', '2024년 1월 15일 새벽 2시부터 4시까지 시스템 점검이 있습니다. 서비스 이용에 불편을 드려 죄송합니다.', true, true, 150, '2024-01-10 10:00:00', '2024-01-10 10:00:00'),
('새로운 기능 출시 안내', '사용자 편의를 위한 새로운 기능이 추가되었습니다. 자세한 내용은 도움말을 참조해주세요.', true, true, 89, '2024-01-12 14:30:00', '2024-01-12 15:20:00'),
('개인정보 처리방침 개정', '개인정보 처리방침이 2024년 2월 1일부터 개정됩니다. 변경사항을 확인해주세요.', false, true, 45, '2024-01-08 09:15:00', '2024-01-08 09:15:00'),
('서비스 이용약관 변경', '서비스 이용약관이 일부 변경되었습니다. 계속 이용하시려면 동의가 필요합니다.', false, true, 67, '2024-01-05 16:45:00', '2024-01-06 10:30:00'),
('임시 서비스 중단 안내', '네트워크 장애로 인한 임시 서비스 중단이 있었습니다. 현재는 정상 운영 중입니다.', false, false, 23, '2024-01-03 11:20:00', '2024-01-03 11:20:00'),
('고객센터 운영시간 변경', '고객센터 운영시간이 평일 09:00~18:00으로 변경됩니다.', false, true, 38, '2024-01-01 08:00:00', '2024-01-01 08:00:00'),
('연말연시 휴무 안내', '12월 30일부터 1월 2일까지 연말연시 휴무입니다. 1월 3일부터 정상 운영됩니다.', false, true, 92, '2023-12-25 17:30:00', '2023-12-25 17:30:00'),
('보안 강화 안내', '계정 보안 강화를 위해 2단계 인증이 도입됩니다. 설정 방법은 공지사항을 확인해주세요.', false, true, 156, '2023-12-20 13:00:00', '2023-12-22 09:45:00');